import { createSlice } from "@reduxjs/toolkit";
export const TokenReducer = createSlice({
    name: 'token',
    initialState: {
        value: '',
    },
    reducers: {
        setToken: (state, action) => {
            state.value = action;
        },
    },
})

export const { setToken } = token.actions;
export default TokenReducer.reducer;
