import React, {useEffect, useState} from "react";
import {Nav, Navbar, NavDropdown} from "react-bootstrap";
import Container from "react-bootstrap/Container";
import {UserContext} from "../form/JoinForm";
import {selectedContext} from "../Main";

const ListHeader = () => {
    const[userList, setUserList] = useState([]);
    useEffect(()=>{
        fetch("/getUserList")
            .then((res)=>{
                return res.json();
            })
            .then((data)=> {
                setUserList(data);
            })
    }, []);

    const loadingRoom = (me, opponents) => {
        selectedContext.name = opponents;
    }


    return (
        <Navbar bg="light" expand="lg">
            <Container className="d-flex justify-content-center">
                <Navbar.Brand href="#home">
                    <span>Direct</span>
                </Navbar.Brand>
                <NavDropdown title="" id="basic-nav-dropdown">
                    {
                        userList.map( (user, idx) => {
                            return <NavDropdown.Item onClick={loadingRoom(UserContext, user.name)}>{idx} {user.name}</NavDropdown.Item>
                        })
                    }
                </NavDropdown>
            </Container>
        </Navbar>
    );
}

export default ListHeader
