import React, {useEffect, useState} from "react";
import {Button, Form, ListGroup} from "react-bootstrap";
import axios from "axios";


const MemberList = () => {

    const formData = new FormData();
    const [members, setMembers] = useState([])
    const [selectedMembers, setSelectedMembers] = useState([])

    const loadMembers = async () => {
        await axios({
            method: "POST",
            url: "/load/members",
            headers: {
                "Content-Type" : "application/json",
            },
            data: localStorage.getItem("token"),
            withCredentials: true,
        }).then(response => {
            setMembers(response.data.data);
        })
    }

    const createRoom = async () => {
        await axios({
            method: "POST",
            url: "/chat/create",
            headers: {
                "Content-Type": "application/json",
            },
            data: selectedMembers,
            withCredentials: true,
        }).then(response => {
            setMembers(response.data.data);
        })
    }

    const onChange = (e) => {
        e.target.checked ? setSelectedMembers([...selectedMembers, e.target.value])
                        : setSelectedMembers(selectedMembers.filter(m => m !== e.target.value));
        console.log(e.target.checked)
        console.log(selectedMembers)
    }

    useEffect(() => {
        loadMembers()
    }, [])

    return (
        <div className="member_list">
            <ListGroup>
                <Form>
                {
                    members.map((member, idx) => {
                        return (
                        <ListGroup.Item key={idx}>
                            <Form.Check aria-label="option 1" className="me-2 d-inline" value={member.name} onClick={onChange}/>
                            {member.name}
                        </ListGroup.Item>)
                    })
                }
                    <Button type="submit" variant="primary" onSubmit={createRoom}>Create</Button>{' '}
                </Form>
            </ListGroup>
        </div>
    )
}

export default MemberList;
