import {apiSlice} from "../../app/api/apiSlice";

export const authApiSlice = apiSlice.injectEndpoints({
    endpoints: builder => ({
        login: builder.mutation({
            query: data => ({
                url: '/login',
                method: 'POST',
                body: data,
                responseHandler: (response) => response.text()
            }),
        }),
    })
})

export const {useLoginMutation} = authApiSlice
